package module_Java;

public class SingleDimension {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] arr = new int[5];
		
		arr[0]=10;
		arr[1]=20;
		arr[2]=30;
		arr[3]=40;
		arr[4]=50;
		
		int[] arr2 = { 2, 4, 6, 8, 10};
		
		for(int i=0; i<arr.length; i++) {
			
			System.out.println(arr[i]);
		}
		
		for(int i=0; i<arr2.length; i++) {
			
			System.out.println(arr2[i]);
		}
		
		
		
		

	}

}
